import React, { Component } from "react";
import { Modal, Button } from "react-bootstrap";
import { connect } from 'react-redux';
import { editActionCreator } from "../action/editActionCreator";
class EditComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            show: false,
            name: '',
            id: ''
        }
    }
    onSubmit(event) {
        this.props.st();
        event.preventDefault();
    }

   
    render() {
        
        
        return (
            <div>
                <Modal show={this.props.show} onHide={this.props.onHide} name={this.props.name} id={this.props.id} animation={false} size="lg"
                aria-labelledby="contained-modal-title-vcenter"
                centered>
                <Modal.Header closeButton>
                    <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header>
                <Modal.Body>



                        <div>
                            <form className="form-inline" onSubmit={(event) => { this.handleSubmit() }}>
                            <div className="form-group form-group-lg">
                                <div className="input-group col-md-11">
                                        <input type="text" className="form-control " placeholder={this.props.id} editable="false" />
                                </div>
                            </div>

                            <div className="form-group form-group-lg">
                                <div className="input-group col-md-11">
                                        <input type="text" className="form-control" id="pwd" placeholder={this.props.name}/>
                                </div>
                            </div>
                            {' '}

                            <button type="submit" className="btn btn-primary btn-lg">Submit</button>
                        </form>
                    </div>





                </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.props.onHide} >
                        Close
          </Button>
                        <Button variant="primary" onClick={this.props.onHide} >
                        Save Changes
          </Button>
                </Modal.Footer>
            </Modal>
                </div>
        );

    }

}
function mapStateToProps(state) {
    return {

        st: state.student_reducer
    }
}
function mapDispatchToProps(dispatch) {

    return {
        st: (id, name) => dispatch(editActionCreator(id, name))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(EditComponent);
