import React, { Component } from "react";

class EditComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            show: this.props.show
        }
    }

    handleShow() {
        this.setState({ show: true });
    }
    handleClose() {
        this.setState({ show: false });
    }


    render() {

        return (
            <Modal show={this.state.show} onHide={() => { this.handleClose() }} animation={false} size="lg"
                aria-labelledby="contained-modal-title-vcenter"
                centered>
                <Modal.Header closeButton>
                    <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header>
                <Modal.Body>



                    <div>
                        <form className="form-inline" >
                            <div className="form-group form-group-lg">
                                <div className="input-group col-md-11">
                                    <input type="text" className="form-control " placeholder={student.id} />
                                </div>
                            </div>

                            <div className="form-group form-group-lg">
                                <div className="input-group col-md-11">
                                    <input type="text" className="form-control" id="pwd" placeholder={student.name} />
                                </div>
                            </div>
                            {' '}

                            <button type="submit" className="btn btn-primary btn-lg">Submit</button>
                        </form>
                    </div>





                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => { this.handleClose() }}>
                        Close
          </Button>
                    <Button variant="primary" onClick={() => { this.handleClose() }}>
                        Save Changes
          </Button>
                </Modal.Footer>
            </Modal>

        );

    }

}
export default EditComponent;
