import React, { Component } from "react";
import { Modal, Button } from "react-bootstrap";
import { connect } from 'react-redux';
import { editActionCreator } from "../action/editActionCreator";
import { addActionCreator } from "../action/addActionCreator";
class EditComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            name: '',
            id: ''
        }
    }
    handleSubmit(event) {
        this.props.st(this.state.id,this.state.name);
        event.preventDefault();
    }
    handleChange(event) {
        this.setState({ id: this.props.id, name: event.target.value });
    }
   
    render() {
        console.log(this.props);
        
        return (

            <Modal show={this.props.show} onHide={this.props.onHide} animation={false} >
                <Modal.Header closeButton>
                    <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header>
                <Modal.Body>{this.props.name}</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={this.props.handleClose}>
                        Close
          </Button>
                    <Button variant="primary" >
                        Save Changes
          </Button>
                </Modal.Footer>
            </Modal>
                
        );

    }

}
function mapStateToProps(state) {
    return {

        st: state.student_reducer
    }
}
function mapDispatchToProps(dispatch) {

    return {
        st: (id, name) => dispatch(editActionCreator(id, name))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(EditComponent);
