import React, { Component } from "react";
import { connect } from 'react-redux';
import { Modal, Button, ButtonToolbar } from "react-bootstrap";
import "../styles/DisplayComponent.css";
import EditComponent from './EditComponent';
class DisplayComponent extends Component {

    constructor(props) {

        super(props);
        this.state = { show: false ,name:''};

    }
    handleShow(n) {
        this.setState({ show: true,name:n });
    }
    handleClose() {
        this.setState({ show: false });
    }
   
    render() {
      
        return (

            <div className="container">
                

                <table className="table table-striped table-bordered" style={{ fontSize: "medium" }}  >
                    <thead>
                        <tr className="thead-dark">
                            <th>Name</th>

                            <th colSpan="2">Actions</th>
                        </tr>
                    </thead>
                    
                    <tbody>
                        {this.props.st.length > 0 ? (
                            this.props.st.map(student => (
                                <tr key={student.id}>
                                    <td>{student.name}</td>
                                    <td>
                                        <button type="button" className="btn btn-danger">
                                            <span className="glyphicon glyphicon-remove"></span>Delete
                                                </button>
                                    </td>
                                    <td>
                                        <button type="button" className="btn btn-primary" onClick={() => { this.handleShow() }}
                                    >
                                            <span className="glyphicon glyphicon-edit"></span>Edit
                                                </button>
                                        <Modal show={this.state.show} onHide={() => { this.handleClose(student.name) }} animation={false} >
                                            <Modal.Header closeButton>
                                                <Modal.Title>Modal heading</Modal.Title>
                                            </Modal.Header>
                                            <Modal.Body>{this.state.name}</Modal.Body>
                                            <Modal.Footer>
                                                <Button variant="secondary" onClick={() => { this.handleClose() }}>
                                                    Close
          </Button>
                                                <Button variant="primary" >
                                                    Save Changes
          </Button>
                                            </Modal.Footer>
                                        </Modal>  
                                                    
                                    </td>
                                  
                                </tr>

                            ))
                        ) : (
                                <tr>
                                    <td colSpan={2}>No users</td>
                                </tr>
                            )}
                    </tbody>
                </table>
                
                

            </div>
        );
    }
}

function mapStateToProps(state) {
    return {

        st: state.student_reducer
    }
}


export default connect(mapStateToProps,null)(DisplayComponent);
