import React, { Component } from "react";
import { Modal, Button } from "react-bootstrap";
import { connect } from 'react-redux';
import { editActionCreator } from "../action/editActionCreator";
import { addActionCreator } from "../action/addActionCreator";
class EditComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            name: '',
            id: ''
        }
    }
    handleSubmit(event) {
        this.props.st(this.state.id,this.state.name);
        event.preventDefault();
    }
    handleChange(event) {
        this.setState({ [event.target.name]: event.target.value });
    }
   
    render() {
        console.log(this.state);
        
        return (
            <div>
                <Modal show={this.props.show} onHide={this.props.onHide} name={this.props.name} id={this.props.id} animation={false} size="lg"
                aria-labelledby="contained-modal-title-vcenter"
                centered>
                <Modal.Header closeButton>
                    <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header>
                <Modal.Body>



                        <div>
                            <form className="form-inline" onSubmit={(event) => { this.handleSubmit() }}>
                            <div className="form-group form-group-lg">
                                    <div className="input-group col-md-11">
                                        <input type="text" className="form-control " name="id" placeholder={this.props.id} value={this.state.id}
                                            onChange={(event) => this.handleChange(event)}
                                        />
                                </div>
                            </div>

                            <div className="form-group form-group-lg">
                                    <div className="input-group col-md-11">
                                        <input type="text" className="form-control" name="name" value={this.state.name}
                                            onChange={(event) => this.handleChange(event)}
                                            placeholder={this.props.name} />
                                </div>
                            </div>
                            {' '}

                            <button type="submit" className="btn btn-primary btn-lg">Submit</button>
                        </form>
                    </div>





                </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.props.onHide} >
                        Close
          </Button>
                        <Button variant="primary" onClick={this.props.onHide} >
                        Save Changes
          </Button>
                </Modal.Footer>
            </Modal>
                </div>
        );

    }

}
function mapStateToProps(state) {
    return {

        st: state.student_reducer
    }
}
function mapDispatchToProps(dispatch) {

    return {
        st: (id, name) => dispatch(addActionCreator(id, name))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(EditComponent);
