import React, { Component } from "react";
import { Modal, Button } from "react-bootstrap";
class EditComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            show: '',
            name: '',
            id: ''
        }
    }

    handleShow() {
        this.setState({ show: false });
    }
    handleClose() {
        this.setState({ show: false });
    }

    componentWillReceiveProps(props) {
        this.setState({
            show: props.show,
            id: props.id,
            name: props.name
        });
    }
    
    render() {
       
        
        return (
            <div>
                <Modal show={this.state.show} onHide={this.setState({ show: false })} animation={false} size="lg"
                aria-labelledby="contained-modal-title-vcenter"
                centered>
                <Modal.Header closeButton>
                    <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header>
                <Modal.Body>



                    <div>
                        <form className="form-inline" >
                            <div className="form-group form-group-lg">
                                <div className="input-group col-md-11">
                                    <input type="text" className="form-control " placeholder={this.state.id} />
                                </div>
                            </div>

                            <div className="form-group form-group-lg">
                                <div className="input-group col-md-11">
                                    <input type="text" className="form-control" id="pwd" placeholder={this.state.name} />
                                </div>
                            </div>
                            {' '}

                            <button type="submit" className="btn btn-primary btn-lg">Submit</button>
                        </form>
                    </div>





                </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={() => { this.setState({ show: false }) }}>
                        Close
          </Button>
                        <Button variant="primary" onClick={() => {
                            this.setState({ show: false })
                        }}>
                        Save Changes
          </Button>
                </Modal.Footer>
            </Modal>
                </div>
        );

    }

}
export default EditComponent;
