import React, { Component } from "react";
import { Form} from "react-bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
class HomeComponent extends Component {

    constructor() {

        super();
        this.state = {

            name: '',
            show_name:''
        }
    }
    show() {

        this.setState({ show_name: this.state.name });
    }

    render() {
        console.log(this.state.show_name);
        return (
            
            <div>
                <Form>
                    <input type="text" placeholder="Enter name" maxLength="8" onChange={(event) => this.setState({ name: event.target.value })} /> {' '}
                    <input type="submit" value="ADD" onSubmit={() => this.show()} />
                                    </Form>

            </div>

               );


    }

}
export default HomeComponent;
