import React, { Component } from "react";
import { connect } from 'react-redux';
import { Modal, Button, ButtonToolbar } from "react-bootstrap";
import "../styles/DisplayComponent.css";
import EditComponent from './EditComponent';
class DisplayComponent extends Component {

    constructor(props) {

        super(props);
        this.state = { show: false };

    }
    handleShow() {
        this.setState({ show: true });
    }
    handleClose() {
        this.setState({ show: false });
    }
   
    render() {
      
        return (

            <div className="container">
                

                <table className="table table-striped table-bordered" style={{ fontSize: "medium" }}  >
                    <thead>
                        <tr className="thead-dark">
                            <th>Name</th>

                            <th colSpan="2">Actions</th>
                        </tr>
                    </thead>
                    
                    <tbody>
                        {this.props.st.length > 0 ? (
                            this.props.st.map(student => (
                                <tr key={student.id}>
                                    <td>{student.name}</td>
                                    <td>
                                        <button type="button" className="btn btn-danger">
                                            <span className="glyphicon glyphicon-remove"></span>Delete
                                                </button>
                                    </td>
                                    <td>
                                        <button type="button" className="btn btn-primary" data-toggle="modal" data-target="#exampleModal"
                                    >
                                            <span className="glyphicon glyphicon-edit"></span>Edit
                                                </button>
                                        <div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                                            <div class="modal-dialog" role="document">
                                                <div class="modal-content">
                                                    <div class="modal-header">
                                                        <h5 class="modal-title" id="exampleModalLabel">Modal title</h5>
                                                        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                                                            <span aria-hidden="true">&times;</span>
                                                        </button>
                                                    </div>
                                                    <div class="modal-body">
                                                        ...
      </div>
                                                    <div class="modal-footer">
                                                        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
                                                        <button type="button" class="btn btn-primary">Save changes</button>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                           
                                                               
                                    </td>
                                  
                                </tr>

                            ))
                        ) : (
                                <tr>
                                    <td colSpan={2}>No users</td>
                                </tr>
                            )}
                    </tbody>
                </table>
                
            </div>
        );
    }
}

function mapStateToProps(state) {
    return {

        st: state.student_reducer
    }
}


export default connect(mapStateToProps,null)(DisplayComponent);
