import React, { Component } from "react";
import { Form} from "react-bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import {addActionCreator} from "../action/addActionCreator";
import { connect } from "react-redux";
import { bindActionCreators } from 'redux';
class HomeComponent extends Component {

    constructor() {

        super();
        this.state = {

            id: '',
            name: ''
            
        }
    }
    

    render() {
        console.log(this.props);
        
        return (
            
            <div>
                
                    <input type="text" name="id" placeholder="Enter ID" maxLength="8" onChange={(event) => this.setState({ [event.target.name]: event.target.value })} /> {' '}
                <input type="text" name="name" placeholder="Enter name" maxLength="8" onChange={(event) => this.setState({ [event.target.name]: event.target.value })} />{' '}
                <button onClick={() => this.props.st}> Click</button>
                
               

            </div>

               );


    }

}
function mapDispatchToProps(dispatch) {
    return {
        st: () => dispatch(addActionCreator(3, 'alok'))
    }
}
export default connect(null,mapDispatchToProps)(HomeComponent);
