import React, { Component } from "react";
import { Modal, Button } from "react-bootstrap";
class EditComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            show: false,
            name: '',
            id: ''
        }
    }

   
    render() {
        
        
        return (
            <div>
                <Modal {...this.props} animation={false} size="lg"
                aria-labelledby="contained-modal-title-vcenter"
                centered>
                <Modal.Header closeButton>
                    <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header>
                <Modal.Body>



                    <div>
                        <form className="form-inline" >
                            <div className="form-group form-group-lg">
                                <div className="input-group col-md-11">
                                        <input type="text" className="form-control " placeholder={this.props.id} />
                                </div>
                            </div>

                            <div className="form-group form-group-lg">
                                <div className="input-group col-md-11">
                                        <input type="text" className="form-control" id="pwd" placeholder={this.props.name} />
                                </div>
                            </div>
                            {' '}

                            <button type="submit" className="btn btn-primary btn-lg">Submit</button>
                        </form>
                    </div>





                </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.props.onHide }>
                        Close
          </Button>
                        <Button variant="primary" onClick={this.props.onHide}>
                        Save Changes
          </Button>
                </Modal.Footer>
            </Modal>
                </div>
        );

    }

}
export default EditComponent;
