import React, { Component } from "react";
import { Form} from "react-bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import {addActionCreator} from "../action/addActionCreator";
import { connect } from "react-redux";
import { bindActionCreators } from 'redux';
class HomeComponent extends Component {

    constructor() {

        super();
        this.state = {

            id: '',
            name: ''
            
        }
    }
    

    render() {
        console.log(this.props);
        
        return (
            
            <div className="container">
                <form className="form-inline">
                    <div className="form-group">
                        <input type="text" className="form-control form-control-lg" name="id" placeholder="Enter ID" maxLength="8" onChange={(event) => this.setState({ [event.target.name]: event.target.value })} /> {' '}
                    </div>

                    <div className="form-group">
                        <input type="text" className="form-control form-control-lg" name="name" placeholder="Enter name" maxLength="8" onChange={(event) => this.setState({ [event.target.name]: event.target.value })} />{' '}
                    </div>

                    <div>
                        <button type="submit" onClick={() => this.props.st()} className="btn btn-default"> Click</button>
                    </div>
                    </form>
               

            </div>

               );


    }

}
function mapDispatchToProps(dispatch) {
    return {
        st: () => dispatch(addActionCreator(3, 'alok'))
    }
}
export default connect(null,mapDispatchToProps)(HomeComponent);
