import React, { Component } from "react";
import { connect } from 'react-redux';
import "../styles/DisplayComponent.css";
class DisplayComponent extends Component {

    constructor(props) {

        super(props);
        this.state = { show: false };

    }
    handleShow() {
        this.setState({ show: true });
    }

    render() {
        console.log(this.state.show);
        return (

            <div className="container">
                

                <table class="table table-striped table-bordered" style={{ fontSize: "medium" }}  >
                    <thead>
                        <tr class="thead-dark">
                            <th>Name</th>

                            <th colSpan="2">Actions</th>
                        </tr>
                    </thead>
                    
                    <tbody>
                        {this.props.st.length > 0 ? (
                            this.props.st.map(student => (
                                <tr key={student.id}>
                                    <td>{student.name}</td>
                                    <td>
                                        <button type="button" class="btn btn-danger">
                                            <span class="glyphicon glyphicon-remove"></span>Delete
                                                </button>
                                    </td>
                                    <td>
                                        <button type="button" class="btn btn-primary" onClick={() => this.handleShow()}>
                                            <span class="glyphicon glyphicon-edit"></span>Edit
                                                </button>
                                 
                                           
                                    </td>
                                </tr>
                            ))
                        ) : (
                                <tr>
                                    <td colSpan={2}>No users</td>
                                </tr>
                            )}
                    </tbody>
                </table>

            </div>
        );
    }
}

function mapStateToProps(state) {
    return {

        st: state.student_reducer
    }
}


export default connect(mapStateToProps)(DisplayComponent);
